import "./MiCuenta.css";
import logoGoogle from "./akar-icons_google-fill.png";
import logoFacebook from "./cib_facebook-f.png";
// MODAL
import React, { useState } from "react";
import Modal from "react-bootstrap/Modal";
// Transiciones
import Collapse from "react-bootstrap/Collapse";

const MiCuenta = () => {
  // CONSTANTES PARA EL MODAL
  const [show, setShow] = useState(false);
  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);
  // CONSTANTES PARA LA TRANCISION
  const [openLogin, setOpenLogin] = useState(false);
  const [openCreate, setOpenCreate] = useState(false);

  return (
    <>
      <label variant="primary" onClick={handleShow}>
        Mi cuenta
      </label>
      <Modal show={show} onHide={handleClose} className="modal-contain">
        <Modal.Body className="modal-body">
          <div className="buttons-contain">
            <button
              className="button-sesion"
              onClick={() => setOpenLogin(!openLogin)}
              aria-controls="example-collapse-text"
              aria-expanded={openLogin}
            >
              Iniciar sesi√≥n
            </button>
            <button
              className="button-sesion"
              onClick={() => setOpenCreate(!openCreate)}
              aria-controls="example-collapse-text"
              aria-expanded={openCreate}
            >
              Crear cuenta
            </button>
          </div>
          <Collapse in={openLogin}>
            <div className="collapse-item" id="example-collapse-text">
              <h3 className="item-titulo">Crear cuenta</h3>
              <span className="item-text">Crea tu cuenta con Google</span>
              <ul className="sesion-redes">
                <li className="sesion-redes-img">
                  <img src={logoGoogle} alt="google" />
                </li>
                <li className="sesion-redes-img">
                  <img src={logoFacebook} alt="Facebook" />
                </li>
              </ul>
              <form className="formulario" action="">
                <label className="formulario-label-tipo1" htmlFor="">
                  nombre de usario
                  <input className="formulario-input" type="text" />
                </label>
                <label className="formulario-label-tipo1" htmlFor="">
                  Correo electronico
                  <input className="formulario-input" type="text" />
                </label>
                <label htmlFor="">
                  Contrase√±a
                  <input className="formulario-input" type="text" />
                </label>
              </form>
              <button className="button-sesion"> Crear cuenta</button>
            </div>
          </Collapse>
        </Modal.Body>
      </Modal>
    </>
  );
};

export default MiCuenta;
